import {makeAutoObservable} from "mobx";

export default class DeviceStore{
    constructor() {
        this._types = [

            {id: 1, name: 'Традиционный тайский массаж'},
            {id: 2, name: 'Расслабляющий массаж'},
            {id: 3, name: 'Парный массаж'},
            {id: 4, name: 'СПА-уход за телом'},
            {id: 5, name: 'СПА-уход за лицом'},
            {id: 6, name: 'СПА-уход для рук и ног'},
            {id: 7, name: 'СПА-коррекция фигуры'},
            {id: 8, name: 'СПА-день'},
            {id: 9, name: 'Для детей'},
            {id: 10, name: 'Курсы'},
            {id: 11, name: 'Сертификаты на сумму'}

        ]
        this._devices = [
            {id: 1, name:'Традиции Тайланда: общий', price: '3000', img: 'f7abf590-a78c-4ba5-a652-61ba64735e86.jpg'},
            {id: 2, name:'Традиции Тайланда: голова и лицо', price: '2800', img: '1e92e15b-0fa0-4691-a720-c3e479bb8228.jpg'},
            {id: 3, name:'Традиции Тайланда: спина, шея и плечи', price: '2800', img: 'e7a6ac29-29c2-4269-ae2c-e1efa5eafd21.jpg'},
            {id: 4, name:'Рефлексотерапия стоп', price: '2800', img: '8998b7fe-ea55-4665-830c-d6ba364690b2.jpg'}
        ]
        this._selectedType = {}
        makeAutoObservable(this)
    }

    setTypes(types){
        this._types = types
    }
    setDevices(devices){
        this._devices = devices
    }

    setSelectedType(type){
        this._selectedType = type
    }

    get types(){
        return this._types
    }

    get devices() {
        return this._devices   }

    get selectedType() {
        return this._selectedType   }
}
