import React, {useContext, useEffect, useState} from 'react';
import {Button, Col, Dropdown, Form, Modal, Row} from "react-bootstrap";
import {Context} from "../../index";
import {fetchDevices, fetchTypes} from "../../http/deviceApi";
import {observer} from "mobx-react-lite";

const CreateDevice = observer(({show, onHide}) => {
    const {device} = useContext(Context)
    const [name, setName] = useState('')
    const [price, setPrice] = useState(0)
    const [file, setFile] = useState(null)
    const [type, setType] = useState(null)
    const [info, setInfo] = useState([])

    useEffect(() => {
        fetchTypes().then(data => device.setTypes(data))
    }, [])

    const addInfo = () =>{
        setInfo([...info, {description: '', number: Date.now()}])
    }

    const selectFile = e =>{
        setFile(e.target.files[0])
    }

    const changeInfo = (ke)

    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить новую услугу
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Dropdown>
                        <Dropdown.Toggle>{"Выберите тип" || device.selectedType}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {device.types.map(type =>
                                <Dropdown.Item onClick={() => device.setSelectedType(type)}>{type.name}</Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Form.Control
                        value={name}
                        onChange={e => setName(e.target.value)}
                        placeholder="Введите название услуги"/>
                    <Form.Control className={'mt-3'}
                                  value={price}
                                  onChange={e => setPrice(Number(e.target.value))}
                                  placeholder="Введите стоимость услуги"
                                  type='number'/>
                    <Form.Control className={'mt-3'}
                                  onChange={selectFile}
                                  type='file'/>
                    <Button variant={'outline-dark'} onClick={addInfo}>Добавить услугу</Button>

                    <Row>
                        <Col md={6}>
                            <Form.Control
                                placeholder={'Введите описание свойства'}
                            />
                        </Col>

                    </Row>


                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={onHide}>Закрыть</Button>
                <Button onClick={onHide}>Добавить</Button>

            </Modal.Footer>
        </Modal>
    );
});

export default CreateDevice;
